import { Router } from "express";
import { getPage, getClassic, postClassic, getEmoji, getAbility, getQuote, getSplash, postQuote, postAbility, postKey, postAbilityName, postSplash, postSplashName, postEmoji, getPrivacy, } from "../controllers/championsController.js";
const router = Router();
router.get("/privacy", getPrivacy);
router.get("/classic", getClassic);
router.post("/classic", postClassic);
router.get("/emoji", getEmoji);
router.post("/emoji", postEmoji);
router.get("/ability", getAbility);
